import React, { useState } from 'react';
import {graphql} from 'react-relay';
import {
  RelayEnvironmentProvider,
  loadQuery,
  usePreloadedQuery,
  useMutation,
  useQueryLoader,
  commitMutation
} from 'react-relay/hooks';
import RelayEnvironment from '../RelayEnvironment';

import { PageHeader, Input, Col, Layout, Menu, Typography, Button, Card, Image, Grid, Row } from 'antd';
import { MenuUnfoldOutlined, MenuFoldOutlined,
  UserOutlined,
  ShoppingOutlined,
  LogoutOutlined,
  FireTwoTone,
  PlusOutlined,
  PlusCircleOutlined,
  PlusCircleTwoTone,
  GooglePlusOutlined,
  EllipsisOutlined,
  DeleteOutlined,
  EditOutlined, } from '@ant-design/icons';
import 'antd/dist/antd.css';
import { AddRealtyModal } from '../components/AddRealtyModal';
import { globalContext } from '../context/globalContext';
import { EditRealtyModal } from '../components/EditRealtyModal';

const { Suspense } = React;

export const HomeRealtyQuery = graphql`
  query HomeRealtyQuery {
    realtys {
      id
      title
      description
      price
      address,
      city,
      state,
      cep,
      contractType
      imageUrls
      area
      rooms
      bathroom
      garage
    }
  }
`

const preloadedQuery = loadQuery(RelayEnvironment, HomeRealtyQuery, {
  /* query variables */
});

function Home(props: any) {
  const data = usePreloadedQuery(HomeRealtyQuery, props.preloadedQuery) as any;

  const { saveUserInfo } = globalContext();

  const [collapsed, setCollapsed] = useState<boolean>(false);
  const [addModal, setAddModal] = useState<boolean>(false);
  const [editModal, setEditModal] = useState<boolean>(false);
  const [edittingRealty, setEdittingRealty] = useState<any>({});

  const {Header, Content, Footer, Sider} = Layout;
  const { Title, Paragraph, Text } = Typography;

  const handleDeleteRealty = (id: string) => {
    const mutation = graphql`
      mutation HomeDeleteRealtyMutation($id: String!) {
        deleteRealty(id: $id) {
          id
        }
      }
    `;

    const variables = {
      id
    };

    commitMutation(RelayEnvironment, {
      mutation,
      variables,
      updater: (store) => {
        const loadedRealtys = store.getRoot().getLinkedRecords('realtys');
        const newRealtys = loadedRealtys?.filter((realty) => realty.getDataID() !== id);
        store.getRoot().setLinkedRecords(newRealtys, 'realtys');
      }
    })
  }

  return (
    <Layout className='site-layout-background' style={{ minHeight: '100vh' }}>
      <Sider trigger={null} collapsible collapsed={collapsed}>
        <div
          style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            padding: '24px',
          }}
        >
          <FireTwoTone twoToneColor={"#06A77D"} style={{fontSize: 48}} />
        </div>
        <Menu
          theme="dark"
          mode="inline"
          defaultSelectedKeys={['1']}
          items={[
            {
              key: '1',
              icon: <ShoppingOutlined />,
              label: 'Minha Vitrine',
            },
            {
              key: '2',
              icon: <UserOutlined />,
              label: 'Perfil',
              onClick: () => {
                window.location.href = '/profile';
              }
            },
            {
              key: '3',
              icon: <LogoutOutlined />,
              label: 'Logout',
              onClick: () => {
                saveUserInfo('');
                window.location.replace('/signin');
              }
            },
          ]}
        />
      </Sider>
      <Layout>
        <Header
          style={{
            display: 'flex',
            flexDirection: 'row',
            alignItems: 'center',
            justifyContent: 'flex-start',
            padding: '12px 24px',
          }}
        >
            {collapsed ? <MenuUnfoldOutlined style={{color: '#f5f5f5', fontSize: 18}} onClick={() => setCollapsed(false)} /> : <MenuFoldOutlined style={{color:"#F5F5F5", fontSize: 18}} onClick={() => setCollapsed(true)} />}
            <PageHeader title={<Title level={2} style={{color: '#F5F5F5', margin: 0, paddingLeft: 24}}>Minha Vitrine</Title>} style={{ padding: 0 }}/>
        </Header>
        <Content
          className="site-layout-background"
          style={{
            margin: '24px 16px',
            padding: 24,
            minHeight: 280,
          }}
        >
        <Row justify="start" >
          {data ? data.realtys && data.realtys.map((realty: any) => (
            <Col xs={24} sm={24} md={12} lg={8} style={{padding: 8}}>
              <Card bordered={false} 
                  draggable
                  style={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    justifyContent: 'center',
                  }}
                  
                  key={realty.id}
                >
                  <Image 
                    src={realty.imageUrls[0]} 
                    style={{maxHeight: 200, marginBottom: 12, width: '100%'}} 
                    fallback="data:image/png;base64,"
                   />
                    <Title level={4}>{realty.title}</Title>
                    <Paragraph>{realty.description}</Paragraph>
                    <div style={{justifyContent: 'space-between', display: 'flex'}}>
                    <Text>R$ {realty.price}</Text>
                      <div>
                        <Button type='primary'>ALUGAR</Button>
                        <Button icon={<EditOutlined key="edit" />} style={{marginLeft: 8}}  onClick={() => {
                          setEditModal(true);
                          setEdittingRealty({...realty});
                        }} />
                        <Button icon={<DeleteOutlined key="delete" />} style={{marginLeft: 8, color: '#ff4444'}}  onClick={() => handleDeleteRealty(realty.id)} />
                      </div>
                    </div>
                </Card>
            </Col>
          )) : (
            <Col xs={24} sm={24} md={12} lg={8} style={{padding: 8}}>
            <Card bordered={false} 
                draggable
                style={{
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  justifyContent: 'center',
                }}
                loading
              >
                <Image 
                  style={{maxHeight: 200, marginBottom: 12, width: '100%'}} 
                  fallback="data:image/png;base64,"
                 />
                  <Title level={4}>Title</Title>
                  <Paragraph>Description</Paragraph>
                  <div style={{justifyContent: 'space-between', display: 'flex'}}>
                  <Text>R$ Price</Text>
                    <div>
                      <Button type='primary'>ALUGAR</Button>
                      <Button icon={<EditOutlined key="edit" />} style={{marginLeft: 8}} />
                      <Button icon={<DeleteOutlined key="delete" />} style={{marginLeft: 8, color: '#ff4444'}}/>
                    </div>
                  </div>
              </Card>
          </Col>
          )}
        </Row>
        </Content>
        <Footer style={{display: 'flex', alignItems: 'center', justifyContent: 'center', padding: 8}}>
          Made with ❤️ by Nilton
        </Footer>
        <Button type="primary" onClick={() => setAddModal(true)} style={{position: 'absolute', right: 24, bottom: 24, height: 56, width: 56, borderRadius: '50%'}}>
          <PlusOutlined />
        </Button>
        {addModal && (
          <AddRealtyModal setAddModal={setAddModal} />
        )}
                        {editModal && (
                  <EditRealtyModal setEditModal={setEditModal} realty={edittingRealty}/>
                )}
      </Layout>
    </Layout>
  );
}

function HomeRoot(props: any) {
  const {Title} = Typography;
  return (
    <RelayEnvironmentProvider environment={RelayEnvironment}>
      <Suspense fallback={
                    <Layout style={{background: '#001529', width: '100vw', height: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center'}}>
                    <div style={{textAlign: 'center', padding: '20px'}}>
                      <FireTwoTone twoToneColor={"#06A77D"} style={{fontSize: 48}} />
                      <Title level={3} style={{color: '#F5F5F5', margin: 24}}>Loading...</Title>
                    </div>
                </Layout>
      }>
        <Home preloadedQuery={preloadedQuery} />
      </Suspense>
    </RelayEnvironmentProvider>
  );
}

export default HomeRoot;